	.login-form {
		margin: -9px 10px 0 10px;
		height: auto;
		background: #007AFF; 
		border-radius: 10px;
		background-color: #EEEEEE;
		box-shadow: 0 2px 10px #9B9B9B;
		padding: 20px;
	}
	.form-input input {
		background: #ffffff;
		border-radius: 5px;
		height: 40px;
		margin: 20px 0;
		padding: 0 10px;
	}

	.login-img {
		display: flex;
		justify-content: center;
		align-items: center;
		height: 108px;
		margin-top: 100upx;
	}

	.owl-login {
		width: 211px;
		height: 108px;
		background-image: url('data:image/png;base64,');
		background-repeat: no-repeat;
		background-size: 100%;
	}


</style>
